import styled from "styled-components";
import bandera from "../assets/img/bandera.png";
import logoRedbee from "../assets/img/redbee_logo.png";
import me from "../assets/img/aboutme.jpg";
import { ThemeProps } from "../commons/ThemeProps";

export const AboutMe = ({ theme }: ThemeProps) => (
  <GradientContainer className={theme}>
    <SolidContainer className={theme}>
      <FlexContainerRow id="about-me">
        <MyItems>
          <AboutMeImage src={me} />
        </MyItems>
        <MyDescription>
          I'm Nico a software engineer currently working in redbee studios,
          taking client ideas to next level, using agile methodologies and good
          practices in Buenos Aires, Argentina. üöÄ
          <div>
            <CustomLink
              href="https://www.youtube.com/watch?v=bbYfjTrX1lw"
              target="_blank"
              rel="noreferrer"
            >
              üéß Electronic music producer
            </CustomLink>
          </div>
          üìï IT Profressor.
          <div>
            üìç I'm from Buenos Aires, Argentina
            <LocationImage src={bandera} />
          </div>
          <div>
            üíº I'm currently working at
            <Redbee target="_blank" href="https://www.redbee.io/">
              <RedbeeImage src={logoRedbee} />
            </Redbee>
          </div>
        </MyDescription>
      </FlexContainerRow>
    </SolidContainer>
  </GradientContainer>
);

export default AboutMe;

const CustomLink = styled.a`
  text-decoration: none;
  transition: 0.2s;
  &:hover {
    font-size: 25px;
  }
`;
const SolidContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-items: center;
  border-radius: 30px;
  padding: 2em;
  width: 70%;
  margin: 2em 0;
  box-sizing: border-box;
  box-shadow: 0 0 9px 0 #00082935;
  backdrop-filter: blur(5px);
  z-index: 1;

  &.light {
    background: #ffffff45;
    a {
      color: black;
    }
  }
  &.dark {
    background: #0008297d;
    color: white;
    a {
      color: white;
    }
  }
  @media (max-width: 768px) {
    padding: 1em;
    width: 90%;
  }
`;
const GradientContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-size: 300% 300%;
  height: 100vh;
  width: 100%;

  &.light {
    background-image: linear-gradient(
      -45deg,
      rgba(59, 173, 227, 1) 0%,
      rgba(87, 111, 230, 1) 25%,
      #2037a9 51%,
      #001348 100%
    );
  }
  &.dark {
    background-image: linear-gradient(
      -45deg,
      rgba(59, 173, 227, 1) 0%,
      rgba(87, 111, 230, 1) 25%,
      #b4009c 51%,
      #6300b4 100%
    );
  }

  animation: AnimateBG 5s ease infinite;

  @keyframes AnimateBG {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;
const FlexContainerRow = styled.div`
  display: flex;
  margin: auto;
  @media (max-width: 768px) {
    flex-direction: column;
    height: auto;
  }
`;
const LocationImage = styled.img`
  width: 20px;
  transition: 0.2s;
  margin: 0 10px;
  &:hover {
    transform: scale(1.2);
  }
`;

const RedbeeImage = styled.img`
  width: 90px;
  padding-left: 20px;
  transition: 0.2s;

  &:hover {
    transform: scale(1.2);
  }
`;

const AboutMeImage = styled.img`
  width: 40%;
  border-radius: 10px;
`;

const Redbee = styled.a``;

const MyItems = styled.div`
  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
  }
`;

const MyDescription = styled.div`
  display: flex;
  flex-direction: column;

  width: 50%;
  padding: 1em;

  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
    margin: auto;
    height: auto;
  }
`;
