import styled from "styled-components";
import me from "../assets/img/aboutme.jpg";
import { ThemeProps } from "../commons/ThemeProps";
import { FlexContainerRow } from "../styles/Grid";

export const AboutMe = ({ theme }: ThemeProps) => (
  <FlexContainerRow id="about-me">
    <PictureSection>
      <AboutMeImage src={me} />
    </PictureSection>
    <DescriptionSection>
      I'm Nico a software engineer currently working in redbee studios, taking
      client ideas to next level, using agile methodologies and good practices
      in Buenos Aires, Argentina. ðŸš€
    </DescriptionSection>
  </FlexContainerRow>
);

export default AboutMe;

const AboutMeImage = styled.img`
  width: 100%;
  border-radius: 10px;
  object-fit: cover;
`;

const PictureSection = styled.div`
  width: 40%;
  margin-right: 2rem;
  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
  }
`;

const DescriptionSection = styled.div`
  display: flex;
  flex-direction: column;

  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
    margin: auto;
    height: auto;
  }
`;

export const SolidContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-items: center;
  border-radius: 30px;
  padding: 2em;
  width: 70%;
  margin: 2em 0;
  box-sizing: border-box;
  box-shadow: 0 0 9px 0 #00082935;
  backdrop-filter: blur(5px);
  z-index: 1;

  &.light {
    background: #ffffff45;
    a {
      color: black;
    }
  }
  &.dark {
    background: #0008297d;
    color: white;
    a {
      color: white;
    }
  }
  @media (max-width: 768px) {
    padding: 1em;
    width: 90%;
  }
`;
