import "react-multi-carousel/lib/styles.css";
import styled from "styled-components";
import { RelativeContainer, Title } from "../Styles";
import factorit from "./../../assets/img/experience/factorit.svg";
import glamit from "./../../assets/img/experience/glamit.svg";
import redbee from "./../../assets/img/experience/redbee.svg";
import sanju from "./../../assets/img/experience/sanju.png";
import { Stepper } from "./Stepper";
import { TimelineCard } from "./TimelineCard";

const steps = [
  <TimelineCard
    image={sanju}
    classname="sjt"
    location="right"
    title="Instituto TÃ©cnico Industrial San Judas Tadeo"
    position="Profesor"
    description={"IT professor, algorythms and C develompent."}
  />,
  <TimelineCard
    image={factorit}
    classname="fit"
    title="FactorIT"
    location="left"
    position="Fullstack developer"
    description={"Web development, working building Walmart apps."}
  />,
  <TimelineCard
    image={glamit}
    classname="glamit"
    title="Glamit"
    location="right"
    position="Front end developer"
    description={
      "Frontend developer using ReactJS and Less, Ecommerce builder."
    }
  />,

  <TimelineCard
    image={redbee}
    title="redbee"
    classname="redbee"
    location="left"
    position="Fullstack developer"
    description={
      "Web and Mobile development. Front and back end using Flutter and Java. Part of Partners pics build."
    }
  />,
];

export const Experience = () => {
  return (
    <RelativeContainer>
      <FlexContainerColumn id="experience">
        <Title>My career</Title>
      </FlexContainerColumn>
      <Container>
        <Lines />
        {steps.map((s) => {
          return s;
        })}
      </Container>
    </RelativeContainer>
  );
};

const FlexContainerColumn = styled.div`
  margin: auto;
  display: flex;
  justify-content: center;
  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
  }
`;

const Container = styled.div`
  overflow: hidden;

  @media (max-width: 768px) {
    width: 100%;
    padding: 0;
  }
`;

const Lines = styled.div`
  .box {
    .container {
      display: flex;

      .lines {
        margin-left: 40px;
        margin-top: 6px;
        transform: translateY(50px);
        .dot {
          width: 14px;
          height: 14px;
          background: #d1d6e6;
          border-radius: 7px;
        }

        .line {
          height: 103px;
          width: 2px;
          background: #d1d6e6;
          margin-left: 5.3px;
        }
      }
      .cards {
        margin-left: 12px;
        .card {
          width: 249px;
          height: 93px;
          padding-top: 25px;
          background: #ffffff;
          box-shadow: 0 2px 2px 0 #eeeeee40;
          border-radius: 10px;
          box-shadow: 0px 16px 15px -10px rgba(105, 96, 215, 0.0944602);
          margin-bottom: 10px;

          &.mid {
            height: 71px;
          }

          h4 {
            font-family: Open Sans;
            font-style: normal;
            font-weight: bold;
            font-size: 14px;
            line-height: 19px;
            margin-left: 25px;
            margin-bottom: 5px;

            color: #2b2862;
          }

          p {
            font-family: Open Sans;
            font-style: normal;
            font-weight: normal;
            font-size: 16px;
            line-height: 22px;

            color: #2b2862;
            margin-left: 25px;
          }
        }
      }
    }

    .bottom {
      width: 100%;
      height: 107px;
      background: #fff;
      box-shadow: 0 0 2px #eeeeee50;
      padding-top: 45px;

      .btn {
        width: 249px;
        height: 62px;
        background: #ffffff40;
        mix-blend-mode: normal;
        cursor: pointer;
        border: 1px solid #8260d780;
        border-radius: 5px;

        display: flex;
        align-items: center;
        justify-content: center;
        font-family: Open Sans;
        font-style: normal;
        font-weight: bold;
        font-size: 14px;
        line-height: 19px;
        color: #2b2862;

        margin-left: 53px;
        transition: 0.3s;
        background: #2b2862;
        color: #fff;
        border-color: #2b2862;

        &:hover {
          transform: scale(1.03);
        }
      }
    }
  }
`;
